CREATE TABLE LANCAMENTO (
	LANC_ID BIGINT(20) PRIMARY KEY AUTO_INCREMENT,
	LANC_DESCRICAO VARCHAR(50) NOT NULL,
	LANC_DT_VENCIMENTO DATE NOT NULL,
	LANC_DT_PAGAMENTO DATE,
	LANC_VALOR DECIMAL(10,2) NOT NULL,
	LANC_OBSERVACAO VARCHAR(200),
	LANC_TIPO CHAR(7) NOT NULL,
	CAT_ID BIGINT(20) NOT NULL,
	PES_ID BIGINT(20) NOT NULL,
	FOREIGN KEY (CAT_ID) REFERENCES CATEGORIA(CAT_ID),
	FOREIGN KEY (PES_ID) REFERENCES PESSOA(PES_ID)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




